import React, { useState, useEffect, Fragment } from 'react';
// import Layout from '../core/Layout';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.min.css';
import { Link } from 'react-router-dom';
import { isAuth, getCookie } from '../../../auth/helpers';



const UserNotes = ({ history }) => {
    const [values, setValues] = useState({
        title: "",
        category: "",
        body: "",
        UserId: "",
        isBookmarked: false,
        LessonId: "",
        bookcategory: "",
        showHideEdit: false
    });

    

    const token = getCookie('token');

    useEffect(() => {
        loadProfile();
    }, []);

    const handleChange = (name) => event => {
        setValues({ ...values, title: title, category: category, body: body, bookcategory: bookcategory, [name]: event.target.value })
    }

    const loadProfile = () => {
        //grabbing the URL from the window
        const URLarray = window.document.URL.split("/")
        const URLarrVar = URLarray[URLarray.length-1]
        const URL_id = URLarrVar.split("?")

        //GET the correct logged user
        axios({
            method: 'GET',
            url: `${process.env.REACT_APP_API}/user/${isAuth()._id}`,
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(response => {
                //set all the values of the user
                console.log("URLid", URL_id[0])

                if(response.data.notes.length === 0) {
                    //if the user has notes identify if there are notes that belong to this lesson
                } else { 
                    response.data.notes.map ( note => {
                        if (note.Lesson_id === URL_id[0]){
                            setValues({...values, title: note.title, category: note.category, body: note.body})
                            //notes that do not belong to this lesson are reported in console.
                        } 
                    })
                }
                //if the user has 0 bookmarks return nothing
                if (response.data.bookmarks.length === 0) {
                    console.log("Lesson was not bookmarked")
                    //if the user has bookmarks identify if there are bookmarks that belong to this lesson
                } else {
                    response.data.bookmarks.map( bookmark => {
                        if (response.data.bookmark.Lesson_id === LessonId) {
                            setValues({ ...values, bookcategory: bookmark.category, isBookmarked: true})
                            //bookmakrs that belong to this lesson are reported in console.
                        }
                    })
                }
               
                const {  _id } = response.data;
                setValues({ ...values, UserId});
                
            })
    };

    const { UserId, title, category, body, LessonId, isBookmarked, bookcategory } = values;

    //show notes in form
    const handleEditNotes = (noteTitle, noteCategory, noteBody, noteLink, noteDate, noteId) => {
        handleChange(noteTitle,noteBody,noteCategory);
        return (
            <Fragment>
                <div>
                    <form>
                        <input placeholder={"Title: " + noteTitle} onChange={handleChange('title')} type="text" name="title" className="form-control" id="title"></input>
                        <Link className="nav-link" to={'/lesson/' + noteLink}>Click to go to lesson...</Link>
                        <input placeholder={"Category: " + noteCategory} onChange={handleChange('category')} name="category" type="text" className="form-control" id="category"></input>
                        <textarea className='note-textbox' onChange={handleChange('body')} id="bodyNotes" name="body">{noteBody}</textarea>
                        <div className='date'>Created on: {convertDate(noteDate)}</div>
                        <button className="btn btn-warning" onClick={() => handleSubmitEdit(noteId, noteLink)} type="submit">Submit Edit</button>
                    </form>
                </div>
                <button type="button" className="btn btn-warning deleteNote-btn" onClick={() => RemoveNote(noteId)}>Delete Note</button>
            </Fragment>
        )
    }

    //convert to date
    const convertDate = (date) => {
        let arr1 = date.split("T")
        let arr2 = arr1[0].split("-")
        let dateyear = arr2[2] + ", " + arr2[0];
        switch (arr2[1]) {
            case '01': return "Jan. " + dateyear;
                break;
            case '02': return "Feb. " + dateyear;
                break;
            case '03': return "March " + dateyear;
                break;
            case '04': return "Apr. " + dateyear;
                break;
            case '05': return "May " + dateyear;
                break;
            case '06': return "June " + dateyear;
                break;
            case '07': return "July " + dateyear;
                break;
            case '08': return "Aug. " + dateyear;
                break;
            case '09': return "Sept. " + dateyear;
                break;
            case '10': return "Oct. " + dateyear;
                break;
            case '11': return "Nov. " + dateyear;
                break;
            case '12': return "Dec. " + dateyear;
                break;
        }

    }

    const RemoveNote = (Note_id) => {
        axios({
            method: 'DELETE',
            url: `${process.env.REACT_APP_API}/notes/` + Note_id,
            headers: {
                Authorization: 'Bearer ' + token
            }
        })
            .then(response => {
                console.log('Note was deleted', response);
            });
    }

    //handle all edits to the notes as a POST
    const handleSubmitEdit = () => {
        const URLarray = window.document.URL.split("/")
        const URLarrVar = URLarray[URLarray.length-1]
        const URL_id = URLarrVar.split("?")
        console.log(URL_id[0])
        console.log(UserId)
        console.log(title, body, category)
        alert("stop")  
        axios({
            method: "POST",
            url: process.env.REACT_APP_API + '/user/note/'+ URL_id[0],
            headers: {
                Authorization: 'Bearer ' + token
            },
            data: { title: title, category: category, body: body, User_id: UserId }
        }).then(response => {
            console.log(title, body, category)
            alert("Never stop!")
        })
    }

//toggle to show notes
    const getUserNote = () =>{
        console.log("You are looking for the notes!")
    }
 
    //toggle to addBookmark
    const addBookmark = () =>{
        console.log("Bookmark time!")
    }

//form for handling the note changes
    const takeNotes = () => {
        return (
        <div>
            <form>
                <input placeholder={"Title: "+ title } onChange={handleChange('title')} type="text" name="title" className="form-control" id="title"></input>
                <input placeholder={"Category: "+ category} onChange={handleChange('category')} name="category" type="text" className="form-control" id="category"></input>
                <textarea className='note-textbox' onChange={handleChange('body')} id="bodyNotes" name="body">{body}</textarea>
                <button className="btn btn-warning" onClick={() => handleSubmitEdit()} type="submit">Submit Edit</button>
            </form>
            <button type="button" className="btn btn-warning deleteNote-btn" onClick={() => RemoveNote()}>Delete Note</button>
        </div>
        )
       }
// what the user is viewing
    return (
        <div>
        <div className="noteContainer">
            {UserId}
            {title}
            {body}
            {takeNotes()}
        </div>
        <div className="bookmarkedPage">
        </div>
{isBookmarked}
        {/* <img src={'/images/notes.png'} onClick={getUserNote} alt="notes" id="notesLessons" />
        <img src={'/images/bookmark.png'} onClick={addBookmark} alt="bookmark" id="bookmarkLessons" /> */}
        </div>
        );
    };

    export default UserNotes;
