import React from 'react';
import ReactDOM from 'react-dom';
import {Editor, EditorState} from 'draft-js';
import "../style.css"

class DraftLesson extends React.Component {
  constructor(props) {
    super(props);
    this.state = {editorState: EditorState.createEmpty()};
    this.onChange = editorState => this.setState({editorState});
}
render() {
    console.log(this.state.editorState)
    return (
        <div className="editorContainer">
            <h3>Type a New Lesson</h3>
        <Editor className="editor-outline" editorState={this.state.editorState} onChange={this.onChange} />
        </div>
    );
  }
}
ReactDOM.render(<DraftLesson />, document.getElementById('root'));

export default DraftLesson;
