import { Button, Modal } from "react-bootstrap"
import { isAuth, getCookie, signout, updateUser } from '../../../../auth/helpers';
import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.min.css';
import axios from 'axios';


const SettingsModal = ({history}) => {

   const state = () => { 
        let show = false
    }
    const [values, setValues] = useState({
        role: '',
        name: '',
        email: '',
        password: '',
        btnText: 'Submit'
    });

    const token = getCookie('token');

    useEffect(() => {
        loadProfile();
    }, []);

    const loadProfile = () => {
        axios({
            method: 'GET',
            url: `${process.env.REACT_APP_API}/user/${isAuth()._id}`,
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(response => {
                console.log('PRIVATE PROFILE UPDATE', response);
                const { role, name, email } = response.data;
                setValues({ ...values, role, name, email });
            })
            .catch(error => {
                console.log('PRIVATE PROFILE UPDATE ERROR', error.response.data.error);
                if (error.response.status === 401) {
                    signout(() => {
                        history.push('/');
                    });
                }
            });
    };

    const { role, name, email, password, btnText } = values;

    const handleChange = name => event => {
        // console.log(event.target.value);
        setValues({ ...values, [name]: event.target.value });
    };

    const clickSubmit = event => {
        event.preventDefault();
        setValues({ ...values, btnText: 'Updating' });
        axios({
            method: 'PUT',
            url: `${process.env.REACT_APP_API}/user/update`,
            headers: {
                Authorization: 'Bearer '+ token
            },
            data: { name, password }
        })
            .then(response => {
                console.log('Profile is updated', response);
                updateUser(response, () => {
                    setValues({ ...values, btnText: 'Updated' });
                    toast.success('Profile updated successfully');
                });
            })
            .catch(error => {
                console.log('Error updating profile', error.response.data.error);
                setValues({ ...values, btnText: 'Update' });
                toast.error(error.response.data.error);
            });
    };

    const updateForm = () => (
        <form>
            <div className="form-group">
                <label className="text-muted">Role</label>
                <input defaultValue={role} type="text" className="form-control" disabled />
            </div>
            <div className="form-group">
                <label className="text-muted">Name</label>
                <input onChange={handleChange('name')} value={name} type="text" className="form-control" />
            </div>

            <div className="form-group">
                <label className="text-muted">Email</label>
                <input defaultValue={email} type="email" className="form-control" disabled />
            </div>

            <div className="form-group">
                <label className="text-muted">Password</label>
                <input onChange={handleChange('password')} value={password} type="password" className="form-control" />
            </div>

            <div>
                <button className="btn btn-primary" onClick={clickSubmit}>
                    {btnText}
                </button>
            </div>
        </form>
    );
    
       const handleModal = () => {
            if ( this.state.show === false ) {
                this.setState({ show : true })
            }
            if ( this.state.show === true ) {
                this.setState({ show : false })
            }
        }

        return (
            <div>
                <Button onClick= {()=> {this.handleModal()}}>Settings</ Button>
                <Modal show={this.state.show}>
                    <Modal.Header><h1>Hello </h1></Modal.Header>
                    <Modal.Body> 

                    </Modal.Body> 
                    <Modal.Footer>
                        <Button onClick={()=> {this.handleModal()}}>Close</Button>
                    </Modal.Footer>
                </Modal>
            </div>
        )
    }


export default SettingsModal;
