import React, { useState, useEffect } from 'react';
import Layout from "../core/Layout";
import axios from 'axios';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.min.css';
import jwt from 'jsonwebtoken';

const ResetPassword = ({ match }) => {
    const [values, setValues] = useState({
        name: '',
        token: '', 
        newPassword: '',
        email: '',
        btnText: 'Update Password'
    })

    useEffect (() => {
        let token = match.params.token
        let { name } = jwt.decode(token)

        if (token) {
            setValues({ ...values, name, email,  token })
        }
    }, [])

    const { name, email, token, newPassword, btnText } = values

    const handleChange = (name) => event => {
        setValues({ ...values, newPassword: event.target.value })
    }

    const handleSubmitForm = event => {
        event.preventDefault();
        setValues({ ...values, btnText: "Submitting" })
            axios({
                method: "PUT",
                url: process.env.REACT_APP_API + '/reset-password',
                data: {email}
            }).then(response => {
                console.log("Reset Completed", response)
                
                toast.success(response.data.message)
                setValues({...values, btnText: "Completed Update"})

            }).catch(error => {
                console.log('Error in reset password request', error.response.data)
                setValues({ ...values, btnText: 'Update Password' });
                toast.error(error.response.data.error)
            })
    }


    const resetPasswordForm = () => (
        <form>
            <div className="form-group" >
                <label className="text-muted">New Password: </label>
                <input onChange={handleChange('newpassword')} value={email} placeholder="Enter New Password" type="password" className="form-control" />
            </div>
            <div>
                <button className="btn btn-warning" onClick={handleSubmitForm} type="submit">{btnText}</button>
            </div>
        </form>
    )

    return (
        <Layout>
            <ToastContainer />
    <h1>Reseting Password for {name}: </h1>
            {resetPasswordForm()}
        </Layout>
    )
}

export default ResetPassword;
