import React, { useState, useEffect, Fragment } from 'react';
import axios from 'axios';
import { isAuth, getCookie, signout } from '../../../auth/helpers';
import 'react-toastify/dist/ReactToastify.min.css';
import { Link } from 'react-router-dom';



const Lesson = ({ history }) => {
    const [values, setValues] = useState({
        notes: [],
        showHideEdit: false
    });

    const token = getCookie('token');

    useEffect(() => {
        loadProfile();
    }, []);

    const loadProfile = () => {
        axios({
            method: 'GET',
            url: `${process.env.REACT_APP_API}/user/${isAuth()._id}`,
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(response => {
                const { role, name, email, notes } = response.data;
                setValues({ ...values, role, name, email, notes });
            })
            .catch(error => {
                if (error.response.status === 401) {
                    signout(() => {
                        history.push('/');
                    });
                }
            });
    };

    const { name, notes } = values;

    const handleEditNotes = (t, c, b, d) => {
        return (
            <Fragment>
                <div>
                    <form>
                    <input placeholder={"Title: " + t} type="text" className="form-control" ></input>
                    <input placeholder={"Category: " + c} type="text" className="form-control" ></input>
                    <textArea className='note-textbox'>{b}</textArea>
                    <div className='date'>{convertDate(d)}</div>
                    <input type='submit'>Button</input>
                    </form>
                </div>
            </Fragment>
        )
    }

    const convertDate = (date) => {
        let arr1 = date.split("T")
        let arr2 = arr1[0].split("-")
        let dateyear = arr2[2] + ", " + arr2[0];
        switch (arr2[1]) {
            case '01': return "Jan. " + dateyear;
                break;
            case '02': return "Feb. " + dateyear;
                break;
            case '03': return "March " + dateyear;
                break;
            case '04': return "Apr. " + dateyear;
                break;
            case '05': return "May " + dateyear;
                break;
            case '06': return "June " + dateyear;
                break;
            case '07': return "July " + dateyear;
                break;
            case '08': return "Aug. " + dateyear;
                break;
            case '09': return "Sept. " + dateyear;
                break;
            case '10': return "Oct. " + dateyear;
                break;
            case '11': return "Nov. " + dateyear;
                break;
            case '12': return "Dec. " + dateyear;
                break;
        }

    }

    return (
        <div className="container">
            <h3>Lesson!</h3>
        </div>
    );
};

export default Lesson;
