import React, { Component } from "react";

class ProfileContainer extends Component {
  constructor() {
    super();
    this.state = {
      showHideFName: true,
      showHideLName: true
    };
    this.hideComponent = this.hideComponent.bind(this);
  }

  hideComponent(name) {
    switch (name) {
      case "showHideFName":
        this.setState({ showHideFName: !this.state.showHideFName });
        break;
      case "showHideLName":
        this.setState({ showHideLName: !this.state.showHideLName });
        break;
      default:
    }
  }

  render() {
    const { showHideFName, showHideLName } = this.state;
    return (
      <div>
        <table>
        <div className="profile-body">
          {showHideFName && (
              <div className="newPost">
              POSTS
          </div>
          )}
          {showHideLName && (
          <div className="bookmarkSection">
          BOOKMARKS
      </div>
          )}
        {showHideLName && (
         <div className="noteSection">
         NOTES
     </div>
          )}

          <tr>
            <td>
              <button onClick={() => this.hideComponent("showHideFName")}>
                Hide First Name
              </button>
            </td>
            <td>
              <button onClick={() => this.hideComponent("showHideLName")}>
                Hide Last Name
              </button>
            </td>
          </tr>
          </div>
        </table>
      </div>
    );
  }
}

export default ProfileContainer;
