import React, { useState, useEffect } from 'react';
// import Layout from '../core/Layout';
import axios from 'axios';
import { isAuth, getCookie } from '../../../auth/helpers';
import 'react-toastify/dist/ReactToastify.min.css';
import './style.css';

const UserOptions = ({ history }) => {
    const [values, setValues] = useState({
        name: "",
        email: "", 
        
    });

    const token = getCookie('token');

    useEffect(() => {
        loadProfile();
    }, []);

    const loadProfile = () => {
        axios({
            method: 'GET',
            url: `${process.env.REACT_APP_API}/user/${isAuth()._id}`,
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(response => {
                console.log('PRIVATE PROFILE UPDATE', response);
                const { role, name, email } = response.data;
                setValues({ ...values, role, name, email });
            })
    };

   const getUserNote = () =>{
       console.log("You are looking for the notes!")
   }

   const addBookmark = () =>{
       console.log("Bookmark time!")
   }

    const { name } = values;

    return (
        <div>
        <img src={'/images/notes.png'} onClick={getUserNote} alt="notes" id="notesLessons" />
        <img src={'/images/bookmark.png'} onClick={addBookmark} alt="bookmark" id="bookmarkLessons" />
        <div></div>
        </div>
    );
};

export default UserOptions;
