import React, { useState, useEffect, Fragment } from 'react';
// import Layout from '../core/Layout';
import axios from 'axios';
import { isAuth, getCookie, signout } from '../../../../../auth/helpers';
import 'react-toastify/dist/ReactToastify.min.css';
import { List, ListNote } from "../List";
import { Link } from 'react-router-dom';
import { toast, ToastType } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.min.css';



const UserNotes = ({ history }) => {
    const [values, setValues] = useState({
        notes: [],
        title: "",
        category: "",
        body: "",
        _id: "",
        showHideEdit: false
    });

    

    const token = getCookie('token');

    useEffect(() => {
        loadProfile();
    }, []);

    const handleChange = (name) => event => {
        console.log(name)
        setValues({ ...values, title: title, category: category, body: body, [name]: event.target.value })
        console.log(title, body, category)
    }

    const loadProfile = () => {
        axios({
            method: 'GET',
            url: `${process.env.REACT_APP_API}/user/${isAuth()._id}`,
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(response => {
                const { role, name, email, notes, _id } = response.data;
                setValues({ ...values, role, name, email, notes, _id});
            })
            .catch(error => {
                if (error.response.status === 401) {
                    signout(() => {
                        history.push('/');
                    });
                }
            });
    };

    const { name, notes, title, body, category, _id } = values;

    //show notes in form
    const handleEditNotes = (noteTitle, c, b, l, d, i) => {
        handleChange(noteTitle,noteBody,noteCategory);
        return (
            <Fragment>
                <div>
                    <form>
                        <input placeholder={"Title: " + t} onChange={handleChange('title')} type="text" name="title" className="form-control" id="title"></input>
                        <Link className="nav-link" to={'/lesson/' + l}>Click to go to lesson...</Link>
                        <input placeholder={"Category: " + c} onChange={handleChange('category')} name="category" type="text" className="form-control" id="category"></input>
                        <textarea className='note-textbox' onChange={handleChange('body')} id="bodyNotes" name="body">{b}</textarea>
                        <div className='date'>Created on: {convertDate(d)}</div>
                        <button className="btn btn-warning" onClick={() => handleSubmitEdit(i, t, c, b, l)} type="submit">Submit Edit</button>
                    </form>
                </div>
                <button type="button" className="btn btn-warning deleteNote-btn" onClick={() => RemoveNote(i)}>Delete Note</button>
            </Fragment>
        )
    }

    //convert to date
    const convertDate = (date) => {
        let arr1 = date.split("T")
        let arr2 = arr1[0].split("-")
        let dateyear = arr2[2] + ", " + arr2[0];
        switch (arr2[1]) {
            case '01': return "Jan. " + dateyear;
                break;
            case '02': return "Feb. " + dateyear;
                break;
            case '03': return "March " + dateyear;
                break;
            case '04': return "Apr. " + dateyear;
                break;
            case '05': return "May " + dateyear;
                break;
            case '06': return "June " + dateyear;
                break;
            case '07': return "July " + dateyear;
                break;
            case '08': return "Aug. " + dateyear;
                break;
            case '09': return "Sept. " + dateyear;
                break;
            case '10': return "Oct. " + dateyear;
                break;
            case '11': return "Nov. " + dateyear;
                break;
            case '12': return "Dec. " + dateyear;
                break;
        }

    }

    const RemoveNote = (Note_id) => {
        axios({
            method: 'DELETE',
            url: `${process.env.REACT_APP_API}/notes/` + Note_id,
            headers: {
                Authorization: 'Bearer ' + token
            }
        })
            .then(response => {
                console.log('Note was deleted', response);
                toast.success('Note was Removed!');
            });
    }

    const handleSubmitEdit = (Note_id, t, c, b, l) => {
        axios({
            method: "PUT",
            url: process.env.REACT_APP_API + '/notes/'+ Note_id,
            headers: {
                Authorization: 'Bearer ' + token
            },
            data: {title: title, category: category, body: body, Lesson_id: l, User_id: _id }
        }).then(response => {
            console.log(title, body, category)
            toast.success(`You have updated notes!`)
        })
    }

    return (
            <div className="container">
                <h3 id="note-name-header"> {name}'s Notes </h3>
                {notes.length ? (
                    <List>
                        {notes.map(note => (
                            <ListNote key={note._id}>
                                {
                                handleEditNotes(note.title, note.category, note.body, note.Lesson_id, note.date, note._id)
                                }
                            </ListNote>
                        ))}
                    </List>
                ) : (
                        <h3>You have no notes saved to your portfolio.</h3>
                    )}

            </div>
        );
    };

    export default UserNotes;
