import React, { Component } from "react";

class Settings extends Component {
  state = { show: false };

  showModal = () => {
    this.setState({ show: true });
  };

  hideModal = () => {
    this.setState({ show: false });
  };

  render() {
    return (
      <main>
        <button type="button" className="settings-btn" onClick={this.showModal}>
          Settings
        </button>
      </main>
    );
  }
}

export default Settings;
