import {Editor, EditorState} from 'draft-js';
import "../style.css"

class DraftLesson extends React.Component {
  constructor(props) {
    super(props);
    this.state = {editorState: EditorState.createEmpty()};
    this.onChange = editorState => this.setState({editorState});
}
render() {
