import React, { useState, useEffect, Fragment } from 'react';
// import Layout from '../core/Layout';
import axios from 'axios';
import { isAuth, getCookie, signout } from '../../../../../auth/helpers';
import 'react-toastify/dist/ReactToastify.min.css';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.min.css';



const UserNotes = ({ history }) => {
    const [values, setValues] = useState({
        notes: [],
        showHideEdit: false

    });

    const token = getCookie('token');

    useEffect(() => {
        loadProfile();
    }, []);

    const loadProfile = () => {
        axios({
            method: 'GET',
            url: `${process.env.REACT_APP_API}/user/${isAuth()._id}`,
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(response => {
                const { role, name, email, notes } = response.data;
                setValues({ ...values, role, name, email, notes });
            })
            .catch(error => {
                if (error.response.status === 401) {
                    signout(() => {
                        history.push('/');
                    });
                }
            });
    };

    const RemoveUser = (Note_id) => {
        axios({
            method: 'DELETE',
            url: `${process.env.REACT_APP_API}/notes/`+ Note_id,
            headers: {
                Authorization: 'Bearer '+ token
            }
        })
            .then(response => {
                console.log('Note was deleted', response);
                    toast.success('Note was Removed!');
                });
    }

    return (
<div></div>
    );
};

export default UserNotes;
