import React, { useState, useEffect } from 'react';
// import Layout from '../core/Layout';
import axios from 'axios';
import { isAuth, getCookie, signout, updateUser } from '../../../../../auth/helpers';
import 'react-toastify/dist/ReactToastify.min.css';
import { List, ListNote} from "./List"


const UserNotes = ({ history }) => {
    const [values, setValues] = useState({
        role: '',
        name: '',
        email: '',
        notes: []
    });

    const token = getCookie('token');

    useEffect(() => {
        loadProfile();
    }, []);

    const loadProfile = () => {
        axios({
            method: 'GET',
            url: `${process.env.REACT_APP_API}/user/${isAuth()._id}`,
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(response => {
                console.log('PRIVATE PROFILE UPDATE', response);
                const { role, name, email, notes} = response.data;
                setValues({ ...values, role, name, email, notes });
            })
            .catch(error => {
                console.log('PRIVATE PROFILE UPDATE ERROR', error.response.data.error);
                if (error.response.status === 401) {
                    signout(() => {
                        history.push('/');
                    });
                }
            });
    };

    const { role, name, notes } = values;

    const convertDate = (date) => {
        console.log("date",date)
        let arr1 = date.split("T")
        let arr2 = arr1[0].split("-")
        let dateyear = arr2[2] +", "+arr2[0];
console.log(arr2)
switch(arr2[1]) {
    case '01': return "Jan."+ dateyear;
    break;
    case '02' : return "Feb. "+ dateyear;
    break;
    case '03' : return "March " + dateyear;
}
        
    }

    return (
            <div className="container">
                <h3> {name}'s Notes </h3>
                {notes.length ? (
          <List>
            {notes.map(note => (
              <ListNote key={note._id}>
                  <input value={note.title} type="text" className="form-control" />
                  <div className='category'>{note.category}</div>
            <textArea>{note.body}</textArea>
                  <div className='date'>{convertDate(note.date)}</div>
            <div className='link'>{note.link}</div>
              </ListNote>
            ))}
          </List>
        ) : (
            <h3>Oh No! We need more books to read! Hurry and save more books!</h3>
          )}

            </div>
    );
};

export default UserNotes;
