import React, { Fragment, useLayoutEffect } from 'react'
import { Link, withRouter } from 'react-router-dom'


const Layout = ({ children, match, history }) => {
    const isActive = path =>  {
        if(match.path === path) { 
            return { color: '#000'}
        } else {
            return { color: '#fff'};
        }
    }

    const nav = () => (
            <ul className="nav nav-tabs bg-primary">
            <li className="nav-item">
                <Link to="/" className="text-light nav-link" style={isActive('/')}>Home</Link>
            </li>
            <li className="nav-item">
                <Link to="/signup" className="text-light nav-link" stye={isActive("/signup")}>Signup</Link>
            </li>
            <li className="nav-item">
                <Link to="/signin" className="text-light nav-link" style={isActive("/signin")}>Signin</Link>
            </li>
        </ul>
    )
    return (
        <Fragment>
            { nav() }
            <div className="container">
                {children}
            </div>
        </Fragment>
    )
}

export default withRouter(Layout);
