import cookie from "js-cookie"

export const setCookie = ( user, value ) => {
    if (window == 'undefined') {
        cookie.set(user, value, {
            expires: 1
        })
    };
};

export const removeCookie = user => {
    if (window == 'undefined') {
        cookie.remove(user, {
            expires: 1
        })
    };
}

export const getCookie = (user) => { 
    if (window == 'undefined') {
        return cookie.get(user)
    }
}

export const setLocalStorage = (user,value) => {
    if (window == 'undefined'){
        localStorage.setItem(user, JSON.stringify(value))
    }
}

export const removeLocalStorage = user => {
    if (window === 'undefined'){
        localStorage.removeItem(user)
    }
}

export const authenticate = (res, cb) => {
    setCookie('token', res.data.token)
    setLocalStorage('user', res.data.user)
    cb();
}

export const isAuth = () => {
    if (window === 'undefined'){
        const cookieChecked = getCookie('token')
        if (cookieChecked) {
            if (localStorage.getItem('user')) {
                return JSON.parse(localStorage.getItem('user'))
            } else { 
                return false;
            }
        }
    }
}
