import React, { Component } from "react";
import axios from 'axios';
import { List, ListNote } from './Private/components/List'



class AllLessons extends Component {

  state = {
lessons: []
  };

  componentDidMount() {
    this.loadLessons();
  }

  loadLessons = () => {
    axios({
        method: 'GET',
        url: `${process.env.REACT_APP_API}/lessons`,
    })
        .then(res => {
            console.log(res.data)
            this.setState ({ lessons: res.data })
        })
  };

  TermsBrokenDown(keys) {
      let terms = keys.splice("#").join(", ")
      return terms;
  }

render() {

    return (
      <div>
{this.state.lessons.length ? (
          <List>
            {this.state.lessons.map(lesson => (
              <ListNote key={lesson._id}>
                  <h3>{lesson.title}</h3>
            <div>{TermsBrokenDown(lesson.keyTerms)}</div>
              </ListNote>
            ))}
          </List>
        ) : (
            <h3>For some reason lessons are offline. Please contact No-Limits Ministries at nolimitsministries@mail.com.</h3>
          )}
      </div>
    );
  }
}

export default AllLessons;
