import { useState } from 'react';

    const Settings = () => {
      const [isShowing, setIsShowing] = useState(false);
    
      function toggle() {
        setIsShowing(!isShowing);
      }
    
      return {
        isShowing,
        toggle,
      }
    };

export default Settings;
