import React from 'react';
import ReactDOM from 'react-dom';
import {Editor, EditorState, RichUtils} from 'draft-js';
import "../style.css"
import 'draft-js/dist/Draft.css'

class DraftLesson extends React.Component {
  constructor(props) {
    super(props);
    this.state = {editorState: EditorState.createEmpty()};
    this.onChange = editorState => this.setState({editorState});
    this.onTab = this.onTab.bind(this);
}

handleKeyCommand(command, editorState) {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      this.onChange(newState);
      return 'handled';
    }
    return 'not-handled';
  }

  onTab = (e) => {
    e.preventDefault();
    let currentState = this.state.editorState

    const selection = currentState.getSelection();
    const blockType = currentState
        .getCurrentContent()
        .getBlockForKey(selection.getStartKey())
        .getType();

    if(blockType === "unordered-list-item" || blockType === "ordered-list-item"){
        this.handleChange(RichUtils.onTab(e, currentState, 3));
    }else{
        let newContentState = Modifier.replaceText(
            currentState.getCurrentContent(),
            currentState.getSelection(),
            '    '
        );
        
        this.handleChange(EditorState.push(currentState, newContentState, 'insert-characters'))
    }
}
  // ...
  onTab(e) {
    e.preventDefault();

    let currentState = this.state.editorState;
    let newContentState = Modifier.replaceText(
      currentState.getCurrentContent(),
      currentState.getSelection(),
      tabCharacter
    );

    this.setState({ 
      editorState: EditorState.push(currentState, newContentState, 'insert-characters')
    });
  }

  onUnderlineClick = () => {
    this.onChange(
        RichUtils.toggleInlineStyle(this.state.editorState, "UNDERLINE")
    );
};

onBoldClick = () => {
    this.onChange(RichUtils.toggleInlineStyle(this.state.editorState, "BOLD"));
};

onItalicClick = () => {
    this.onChange(
        RichUtils.toggleInlineStyle(this.state.editorState, "ITALIC")
    );
};



render() {

    const LessonForm = () => {
        return(
        <form>
            <div className="form-group">
            <label className="settings-label">Lesson Title</label>
            <input type="text" className="form-control settings-form form-lesson-input-fields" />
            </div>
            <div className="form-group">
                <label className="settings-label">List Any Key Search Words</label>
                <input type="text" className="form-control settings-form .form-lesson-input-fields" />
            </div>
        </form>
        )
    }
      
    return (
        <div className="editorContainer">
            <h3 className="editor-heading">Type a New Lesson Below</h3>
            <div className="contains-lesson-form">
                {LessonForm()}
            </div>
            <div className="contains-editor">
        <div className="editor-outline">
            <div className="tool-bar">
            <button onClick={this.onUnderlineClick}>U</button>
				<button onClick={this.onBoldClick}>
					<b>B</b>
				</button>
				<button onClick={this.onItalicClick}>
					<em>I</em>
				</button>
            </div>
        <Editor 
        editorState={this.state.editorState} 
        onChange={this.onChange} 
        onTab={this.onTab}
        handleKeyCommand={this.handleKeyCommand} />
        </div>
        </div>
        </div>
    );
  }
}
ReactDOM.render(<DraftLesson />, document.getElementById('root'));

export default DraftLesson;
