import React, { useState } from 'react';
import  { Link, Redirect } from 'react-router-dom';
import Layout from "../core/Layout";
import axios from 'axios';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.min.css'; 

const Signup = () => {
    const [values, setValues] = useState({
        name: '', 
        email: '', 
        password: '', 
        buttonText: 'Submit'
    })

    const { name, email, password, btnText } = values

    const handleChange = (name, email, password) => event => {

    }

    const handleSubmitForm = event => {
        event.preventDefault()
    }


    const signupForm = () => (
        <form> 
            <div className ="form-group" >
                <label for="name" className="text-muted">Name: </label>
                <imput onChange= { handleChange('name')} type="text" className="form-control" />
            </div>
            <div className ="form-group" >
                <label for="email" className="text-muted">Email: </label>
                <imput name="email" onChange= { handleChange('email')} type="email" className="form-control" />
            </div>
            <div className ="form-group" >
                <label for="password" className="text-muted">Password: </label>
                <imput name="password" onChange= { handleChange('password')} type="password" className="form-control" />
            </div>
            <div>
                <button className="btn btn-warning" onClick={handleSubmitForm} type="submit">{btnText}</button>
            </div>
        </form>
    )

    return(
    <Layout>
        <ToastContainer />
        <h1> Signup </h1> 
        {signupForm}
    </Layout> 
    )
}

export default Signup
