import React, { useState, useEffect, Fragment } from 'react';
// import Layout from '../core/Layout';
import axios from 'axios';
import { isAuth, getCookie, signout } from '../../../../../auth/helpers';
import 'react-toastify/dist/ReactToastify.min.css';
import { List, ListNote } from "./List";
import Button from 'bootstrap/dist/css/bootstrap.min.css';



const UserNotes = ({ history }) => {
    const [values, setValues] = useState({
        notes: [],
        showHideEdit: false
    });

    const token = getCookie('token');

    useEffect(() => {
        loadProfile();
    }, []);


    const handleChange = note => event => {
        setValues({ ...values, [note]: event.target.value });
    };

    const loadProfile = () => {
        axios({
            method: 'GET',
            url: `${process.env.REACT_APP_API}/user/${isAuth()._id}`,
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(response => {
                const { role, name, email, notes } = response.data;
                setValues({ ...values, role, name, email, notes });
            })
            .catch(error => {
                if (error.response.status === 401) {
                    signout(() => {
                        history.push('/');
                    });
                }
            });
    };

    const { name, notes } = values;

    const handleEditNotes = (t, c, b, l, d) => {
        return (
        <Fragment>
                            <input onChange={handleChange('title')} placeholder={"Title: "+ t} type="text" className="form-control" ></input>
                            <div className='category'>{c}</div>
                            <textArea className='note-textbox'>{b}</textArea>
                            <div className='date'>{convertDate(d)}</div>
                            <div className='link'>{l}</div>
                </Fragment>
        )
    }

    const convertDate = (date) => {
        console.log("date", date)
        let arr1 = date.split("T")
        let arr2 = arr1[0].split("-")
        let dateyear = arr2[2] + ", " + arr2[0];
        console.log(arr2)
        switch (arr2[1]) {
            case '01': return "Jan. " + dateyear;
                break;
            case '02': return "Feb. " + dateyear;
                break;
            case '03': return "March " + dateyear;
                break;
            case '04': return "Apr. " + dateyear;
                break;
            case '05': return "May " + dateyear;
                break;
            case '06': return "June " + dateyear;
                break;
            case '07': return "July " + dateyear;
                break;
            case '08': return "Aug. " + dateyear;
                break;
            case '09': return "Sept. " + dateyear;
                break;
            case '10': return "Oct. " + dateyear;
                break;
            case '11': return "Nov. " + dateyear;
                break;
            case '12': return "Dec. " + dateyear;
                break;
        }

    }

    return (
        <div className="container">
            <h3> {name}'s Notes </h3>
            {notes.length ? (
                <List>
                    {notes.map(note => (
                        <ListNote key={note._id}>
<div className="note-title">{note.title}</div>
<div className="note-category">{note.title}</div>
<div className="note-date">{convertDate(note.date)}</div>
<button variant="outline-secondary" onClick={() => this.hideComponent("showHideEdit")}>View Note</button>
{handleEditNotes(note.title, note.category, note.body, note.link, note.date)}
                        </ListNote>
                    ))}
                </List>
            ) : (
                    <h3>You have no notes saved to your portfolio.</h3>
                )}

        </div>
    );
};

export default UserNotes;
