import React, { Component } from "react";


class ProfileContainer extends Component {

    constructor() {
        super();
        this.state = {
            name: "Profile",
            showHideNewPost: false,
            showHideNoteSection: false,
            showHideBookmarkSection: false
        }
        this.hideComponent = this.hideComponent.bind(this);
    }

    hideComponent(name) {
        console.log(name);
        switch (name) {
            case "showHideNewPost":
                this.setState({ showHideNewPost: !this.state.showHideNewPost });
                break;
            case "showHideNoteSection":
                this.setState({ showHideNoteSection: !this.state.showHideNoteSection });
                break;
            case "showHideBookmarkSection":
                this.setState({ showHideBookmarkSection: !this.state.showHideBookmarkSection });
                break;
            default:
        }
    }

    render() {

        const { showHideNewPost, showHideNoteSection, showHideBookmarkSection } = this.state;

        return (
            <div>
                <div className="profile-tabs">
                    <div className="newPost-tab" onClick={() => this.hideComponent("showHideNewPost")} >
                        Click to hide Red
                </div>
                    <div className="noteSection-tab" onClick={() => this.hideComponent("showHideNoteSection")}>
                        Click to hide Blue
                </div>
                    <div className="bookmarkSection-tab" onClick={() => this.hideComponent("showHideBookmarkSection")}>
                        Click to hide Green
                </div>
                </div>
                <div className="profile-body">
                        {showHideNewPost && (
                                    <div className="newPost">
                                    POSTS
                                </div>
                            )}
                        {showHideNoteSection && (
        <div className="noteSection">
        NOTES
    </div>
                            )}
                        {showHideBookmarkSection && (
                                    <div className="bookmarkSection">
                                    BOOKMARKS
                                </div>
                            )}
                <div className="profile-body">

                </div>
                            </div>
            </div>
        )
    }
}
export default ProfileContainer;
