import React, { useState, useEffect } from 'react';
import { Link, Redirect } from 'react-router-dom';
import Layout from '../core/Layout';
import axios from 'axios';
import { isAuth } from '../auth/helpers';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.min.css';

const Private = () => {
    const [values, setValues] = useState({
        name: '',
        password: '',
        btnText: 'Update'
    });

    useEffect(() => {
        loadProfile()
    }, [])

    const loadProfile = () => {
        axios({
            method: 'GET', 
            url: process.env.REACT_APP_API+'/user/'+isAuth()._id
        })
    }

    const { name, password, btnText } = values;

    const handleChange = name => event => {
        setValues({ ...values, [name]: event.target.value });
    };

    const handleSubmit = event => {
        event.preventDefault();
        setValues({ ...values, btnText: 'Updating...' });
        axios({
            method: 'PUT',
            url: process.env.REACT_APP_API+'/user/update',
            data: { name,  password }
        })
            .then(response => {
                console.log('Your profile is updated', response);
                setValues({ ...values, name: '', password: '', btnText: 'Updated' });
                toast.success(response.data.message);
            })
            .catch(error => {
                console.log('Error. Please try again', error.response.data);
                setValues({ ...values, btnText: 'Update' });
                toast.error(error.response.data.error);
            });
    };

    const updateForm = () => (
        <form>
            <div className="form-group">
                <label className="text-muted">Name:</label>
                <input onChange={handleChange('name')} value={name} type="text" className="form-control" />
            </div>

            <div className="form-group">
                <label className="text-muted">Password:</label>
                <input onChange={handleChange('password')} value={password} type="password" className="form-control" />
            </div>

            <div>
                <button className="btn btn-primary" onClick={handleSubmit}>
                    {btnText}
                </button>
            </div>
        </form>
    );

    return (
        <Layout>
            <div className="col-md-6 offset-md-3">
                <ToastContainer />
                <h1 className="pt-5 text-center">Private</h1>
                <p className="lead text-center">Profile update</p>
                {updateForm()}
            </div>
        </Layout>
    );
};

export default Private;
