import React, { useState, useEffect, Fragment } from 'react';
// import Layout from '../core/Layout';
import axios from 'axios';
import { isAuth, getCookie, signout } from '../../../../../auth/helpers';
import 'react-toastify/dist/ReactToastify.min.css';
import { List, ListNote } from "../List";
import { Link } from 'react-router-dom';



const UserNotes = ({ history }) => {
    const [values, setValues] = useState({
        notes: [],
        showHideEdit: false

    });

    const token = getCookie('token');

    useEffect(() => {
        loadProfile();
    }, []);

    const loadProfile = () => {
        axios({
            method: 'GET',
            url: `${process.env.REACT_APP_API}/user/${isAuth()._id}`,
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(response => {
                const { role, name, email, notes } = response.data;
                setValues({ ...values, role, name, email, notes });
            })
            .catch(error => {
                if (error.response.status === 401) {
                    signout(() => {
                        history.push('/');
                    });
                }
            });
    };

    const { name, notes } = values;

    //show notes in form
    const handleEditNotes = (t, c, b, l, d, i) => {
        return (
            <Fragment>
                <div>
                    <input placeholder={"Title: " + t} type="text" className="form-control" id="title"></input>
                    <Link className="nav-link" to={'/lesson/'+ l}>Click to go to lesson...</Link>
                    <input placeholder={"Category: " + c} type="text" className="form-control" id="category"></input>
                    <textArea className='note-textbox' id="bodyNotes">{b}</textArea>
                    <div className='date'>Created on: {convertDate(d)}</div>
                    <button type="button" className="saveNote-btn">Save Edits</button><button type="button" className="deleteNote-btn" onClick={()=> RemoveNote()}>Delete Note</button>
                </div>
            </Fragment>
        )
    }

    //convert to date
    const convertDate = (date) => {
        let arr1 = date.split("T")
        let arr2 = arr1[0].split("-")
        let dateyear = arr2[2] + ", " + arr2[0];
        switch (arr2[1]) {
            case '01': return "Jan. " + dateyear;
                break;
            case '02': return "Feb. " + dateyear;
                break;
            case '03': return "March " + dateyear;
                break;
            case '04': return "Apr. " + dateyear;
                break;
            case '05': return "May " + dateyear;
                break;
            case '06': return "June " + dateyear;
                break;
            case '07': return "July " + dateyear;
                break;
            case '08': return "Aug. " + dateyear;
                break;
            case '09': return "Sept. " + dateyear;
                break;
            case '10': return "Oct. " + dateyear;
                break;
            case '11': return "Nov. " + dateyear;
                break;
            case '12': return "Dec. " + dateyear;
                break;
        }

    }

    const RemoveBookmark = (Bookmark_id) => {
        axios({
            method: 'DELETE',
            url: `${process.env.REACT_APP_API}/bookmarks/`+ Bookmark_id,
            headers: {
                Authorization: 'Bearer '+ token
            }
        })
            .then(response => {
                console.log('Bookmark deleted', response);
                    toast.success('Bookmark was Removed!');
                });
    }

    return (
        <div className="container">
            <h3 id="note-name-header"> {name}'s Notes </h3>
            {notes.length ? (
                <List>
                    {notes.map(note => (
                        <ListNote key={note._id}>
                            {handleEditNotes(note.title, note.category, note.body, note.Lesson_id, note.date, note._id)}
                        </ListNote>
                    ))}
                </List>
            ) : (
                    <h3>You have no notes saved to your portfolio.</h3>
                )}

        </div>
    );
};

export default UserNotes;
