import React, { useState, useEffect } from 'react';
import  { Link, Redirect } from 'react-router-dom';
import Layout from "../core/Layout";
import axios from 'axios';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.min.css';

const Activate = ({match}) => {
    const [values, setValues] = useState({
        name: '',
        token: '', 
        show: true
    });

    useEffect(() => {
        let token = match.params.token
        console.log(token)
    }, [])

    const { name, token } = values

    const handleSubmitForm = event => {
        event.preventDefault();
        setValues({ ...values, btnText: "Submitting" })
            axios({
                method: "POST",
                url: process.env.REACT_APP_API + '/signin',
                data: {token}
            }).then(response => {
                console.log("Signed in!", response)

                //save user, token in cookie
                setValues({ ...values, btnText: "Signed In" });
                toast.success(`Hello ${response.data.user.name}. Welcome back!`)
            }).catch(error => {
                console.log('Error in Signup', error.response.data)
                setValues({ ...values, btnText: 'Submit' });
                toast.error(error.response.data.error)
            })
    }

const activationLink = () => ( 
    <div>
<h1 className="p-5 text-center">Hello {name}. Ready to activate your account?</h1>
<button className="btn btn-outline-warning" onClick={handleSubmitForm()}>Submit</button>
    </div>
)
    return (
        <Layout>
            <ToastContainer />
            <h1> Activate Account </h1>
            {activationLink()}
        </Layout>
    )
}

export default Activate;
