import React, { Component } from "react";
import API from "../../util.js/lessonAPI";


class AllLessons extends Component {

  state = {
lessons: []
  };

  componentDidMount() {
    this.loadLessons();
  }

  loadLessons = () => {
    API.getlessons()
      .then(res =>
        this.setState({ lessons: res.data })
        // console.log(res)
      )
      .catch(err => console.log(err));
  };

  deleteBook = id => {
    API.deleteBook(id)
      .then(res => this.loadBooks())
      .catch(err => console.log(err));
  };

  render() {
    return (
      <div>

      </div>
    );
  }
}

export default AllLessons;
